import React from "react";
import { useSelector } from "react-redux";

export default function Status() {
    const full_data = useSelector(state => state);


    const [inf, setInf] = React.useState({
        'status': 'red',
        'color': 'red'
    })

    function getInfo(){
        return full_data.then(res=>{return res})
    }

    React.useEffect(() => {
        setInf(getInfo().then(res=>{return res}))
        console.log('inf',inf)
    }, []);


    return (
        <div className="status">
            <p>Статус: <span>{inf}</span></p>
        </div>
    );
}
