import React from "react";
import {useSelector} from "react-redux";


export default function Node(props) {

    function get_metrics() {
            return fetch('/api/metrics')
                .then(r => {
                    return r.json()
                })
                .catch(err => {
                    return err
                })
        }

    function get_metrics_by_node(node_name){
    console.log('ok')
           get_metrics().then((metrics) => {
                 let newest_metric;
                 for (const met of metrics) {
                    if (met.caption === node_name) {
                        if (!newest_metric) {
                            newest_metric = met
                        }
                        else {
                            if (met.datetime > newest_metric.datetime) {
                                newest_metric = met
                            }
                        }
                    }
                 }



           }
        )
     }

//     React.useEffect(() => {
//
//         full_data.then(
//             res=> {
//                 setInf(res)
//
//             })
//
//     }, [inf]);

     return (
         <div className="node" onClick={()=> get_metrics_by_node(props.node.node_name)}>
            <p className='node__status' style={{backgroundColor:`${props.node.status_color}`}}></p>
            <p >{props.node.node_name}</p>
         </div>

       )


}
