import React from "react";
import { useSelector } from "react-redux";

export default function Status() {
    const full_data = useSelector(state => state);
    let statuses = {};
    let status_of_service;
    let status_color;
    full_data.then(res=>{
        // eslint-disable-next-line array-callback-return
        res.map((i) => {
            let status = i["status_description"]
            statuses[status] = 1
        })
        if (statuses.hasOwnProperty('CRITICAL')) {
            status_of_service = 'CRITICAL'
            status_color = 'red'
        }
        else{
            if (statuses.hasOwnProperty('WARNING')) {
                status_of_service = 'WARNING'
                status_color = 'yellow'
            }
            else {
                status_of_service = 'OK'
                status_color = 'green'
            }
        }
    })

        statuses['CR'] = 22
        console.log(statuses)

        // for (let key in statuses) {
        //     console.log(key, statuses[key])
        // }






    return (
        <div className="status">
            <p>Статус: <span style={{color:status_of_service['color']}}>{status_of_service['status']}</span></p>
        </div>
    );
}
