import React from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

export default function Groups() {
    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])
    const [groups, setGroups] = React.useState([])
    const dispatch = useDispatch();
    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)
                let un_group = []
                inf.map((i) => {
                    let gr = i["group_name"]
                    if (un_group.indexOf(gr) === -1) {
                        un_group.push(gr)
                    }

                   //  if(groups.includes(gr) === false) {
                   //
                   //      const newGroups = [...groups];
                   //      console.log('new_g', newGroups)
                   //      newGroups.push(gr);
                   //      console.log('2', newGroups)
                   //      setGroups([...newGroups]);
                   // }
                })
                //console.log(un_group)
                setGroups(un_group)

            })

    }, [inf]);


    function chooseGroup(data){
        console.log(data)
    }

    return (
        <div className="groups">
            {groups.map((group, i) => (
                    <p className="group" key={i} onClick={()=>chooseGroup(group)}>{group}</p>
            ))}
        </div>
    );
}
