import React from "react";
import {useSelector} from "react-redux";



export default function Node(props) {

    const [metrics, setMetrics] = React.useState()

    function getColor(metric) {
        if (metric < 85) {
            return "green"
        }
        else if (metric < 95) {
            return "yellow"
        }
        else {
            return "red"
        }
    }

    function get_metrics() {
            return fetch('/api/metrics')
                .then(r => {
                    return r.json()
                })
                .catch(err => {
                    return err
                })
        }

    React.useEffect(() => {
           get_metrics().then((metrics) => {
                 let newest_metric;
                 for (const met of metrics) {

                    if (met.caption === props.node.node_name) {
                        if (newest_metric === undefined) {
                            newest_metric = met
                        }
                        else {
                            if (met.datetime > newest_metric.datetime) {
                                newest_metric = met
                            }
                        }
                    }
                 }
                    setMetrics(newest_metric)
           }
        )
        }, [])

//     React.useEffect(() => {
//
//         full_data.then(
//             res=> {
//                 setInf(res)
//
//             })
//
//     }, [inf]);

     return (
         <div className="node" onClick={()=>{props.chooseNodeStat(props.node)}}>
         <div className='node__discription'>
            <p className='node__status' style={{backgroundColor:`${props.node.status_color}`}}></p>
            <p className='node__name'><span ></span>{props.node.node_name}</p>
         </div>

            { metrics ?
            <div className='node__metrics'>
                <p> CPU: <span style={{color:`${()=>get_metrics(metrics.cpu_utilization)}`}}>{metrics.cpu_utilization}%</span></p>
                <p> RAM: <span>{metrics.memory_utilization}%</span></p>
                <p> DISK: <span>{metrics.disk_utilization}%</span></p>
            </div>
            : null}

         </div>

       )


}
