import React from "react";
import {useSelector} from "react-redux";

export default function SecondColumn() {

    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])

    function get_metrics() {
        return fetch('/api/metrics')
            .then(r => {
                return r.json()
            })
            .catch(err => {
                return err
            })
    }

    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)

            })

    }, [inf]);
    console.log(inf)

    if (inf.length === 0) {
        return (<p>downloading</p>)
    }

    else {
        return (
            <div className="column">

                {inf.map((node, i) => (
                    <div className="node">
                        <p className='node__status' style={{backgroundColor:`${node.status_color}`}}></p>
                        <p onClick={()=> {console.log(get_metrics())}}>{node.node_name}</p>
                    </div>

                ))}

            </div>
        )
    }

}
