import React from "react";
import { CurrentNodeInfo } from "../contexts/CurrentNodeInfo"
import { Chart } from "./NodeChart"

export default function FirstColumn() {

    const [metricsOfNode, setMetricsOfNode] = React.useState();

    function get_metrics() {
        return fetch('/api/metrics')
            .then(r => {
                return r.json()
            })
            .catch(err => {
                return err
            })
    }
    const node = React.useContext(CurrentNodeInfo);
    React.useEffect(() => {
        get_metrics().then((metrics) => {
                const met_arr = []
                for (const met of metrics) {

                    if (met.caption === node.node_name) {
                        console.log(met.caption, node.node_name)
                        met_arr.push(met)
                        //console.log('m', metricsOfNode)
                    }
                }
                setMetricsOfNode([...met_arr]);
                console.log('m', metricsOfNode)
            }
        )
    }, [])


    if (node.node_name) {
        return (
            <div>
                <div>
                    <p>{node.node_name}</p>
                </div>
                {metricsOfNode ? <Chart/> : null}
                <Chart metrics={metricsOfNode}/>
                <div>
                    <p>Interface</p>
                    <p>interface name: {node.interface_name}</p>
                    <p>interface status: {node.interface_status === '3' ? "UP" : "DOWN"}</p>
                </div>
                <div>
                    <p>Администратор: </p>
                    <p>{node.firstname} {node.lastname}</p>
                    <p>{node.email} </p>
                </div>
                <div>
                    <p>Приложения: </p>

                    <p>{node.app_name} </p>
                </div>
            </div>


        );
    }

    else {
        return (
            <div>
                <p>Choose node to get statistic</p>
            </div>
        )
    }

}
