import React from "react";
import {useSelector} from "react-redux";

export default function FirstColumn() {

    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])
    const [statusOfService, setStatusOfService] = React.useState({
        UP: 0,
        DOWN: 0,
        SHUTDOWN:0,
        WARNING: 0,
        CRITICAL: 0,
        UNREACHABLE: 0
    })
    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)
                let statuses = {};
                inf.map((i) => {
                    let status = i["status_description"]
                    if (statuses.hasOwnProperty(status)) {
                        statuses[status] += 1
                    }
                    else {statuses[status] = 1}

                })
                setStatusOfService({...statuses})
                console.log(statusOfService)
                })



        //console.log('ss',  inf[0]["group_name"])

    }, [inf]);


    return (
        <div className="info">
            <p></p>
            <p>Nodes</p>
            <p className="maininfo__critical">Critical</p>
            <p className="maininfo__critical">{statusOfService.CRITICAL ? statusOfService.CRITICAL : 0}</p>
            <p className="maininfo__warning">Warning</p>
            <p className="maininfo__warning">{statusOfService.WARNING ? statusOfService.WARNING : 0}</p>
            <p className="maininfo__ok">OK</p>
            <p className="maininfo__ok">{statusOfService.UP ? statusOfService.UP : 0}</p>
            <p className="maininfo__down">Down</p>
            <p className="maininfo__down">{statusOfService.DOWN ? statusOfService.DOWN : 0}</p>
            <p className="maininfo__shutdown">Shutdown</p>
            <p className="maininfo__shutdown">{statusOfService.SHUTDOWN ? statusOfService.SHUTDOWN : 0}</p>
            <p className="maininfo__unreachable">Unreachable</p>
            <p className="maininfo__unreachable">{statusOfService.UNREACHABLE ? statusOfService.UNREACHABLE : 0}</p>



        </div>
    );
}
