import React from "react";
import {useSelector} from "react-redux";

export default function FirstColumn() {

    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])
    const [statusOfService, setStatusOfService] = React.useState({
        UP: 0,
        DOWN: 0,
        SHUTDOWN:0,
        WARNING: 0,
        CRITICAL: 0,
        UNREACHABLE: 0
    })
    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)
                let statuses = {};
                inf.map((i) => {
                    let status = i["status_description"]
                    if (statuses.hasOwnProperty(status)) {
                        statuses[status] += 1
                    }
                    else {statuses[status] = 1}

                })
                setStatusOfService({...statuses})

                })

    }, [inf]);


    return (
        <div className="info">
            <p></p>
            <p>Nodes</p>
            <p>Critical</p>
            <p>{statusOfService.CRITICAL ? statusOfService.CRITICAL : 0}</p>
            <p>Warning</p>
            <p>{statusOfService.WARNING ? statusOfService.WARNING : 0}</p>
            <p>OK</p>
            <p>{statusOfService.UP ? statusOfService.UP : 0}</p>
            <p>Down</p>
            <p>{statusOfService.DOWN ? statusOfService.DOWN : 0}</p>
            <p>Shutdown</p>
            <p>{statusOfService.SHUTDOWN ? statusOfService.SHUTDOWN : 0}</p>
            <p>Unreachable</p>
            <p>{statusOfService.UNREACHABLE ? statusOfService.UNREACHABLE : 0}</p>



        </div>
    );
}
