import React from "react";
import {useSelector} from "react-redux";

export default function FirstColumn() {

    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])
    const [statusOfService, setStatusOfService] = React.useState({
        'status': '',
        'color': ''
    })
    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)
                let statuses = {};
                inf.map((i) => {
                    let status = i["status_description"]
                    if (statuses.hasOwnProperty(status)) {
                        statuses[status] += 1
                    }
                    else {statuses[status] = 1}

                })
                if (statuses.hasOwnProperty('CRITICAL')) {
                    setStatusOfService({
                        'status': 'CRITICAL',
                        'color': 'red'
                    })
                }
                else{
                    if (statuses.hasOwnProperty('WARNING')) {
                        setStatusOfService({
                            'status': 'WARNING',
                            'color': 'yellow'
                        })
                    }
                    else {
                        setStatusOfService({
                            'status': 'OK',
                            'color': 'green'
                        })
                    }
                }
            })

        //console.log('ss',  inf[0]["group_name"])

    }, [inf]);


    return (
        <div className="info">
            <p></p>
            <p>Up</p>
            <p>Down</p>
            <p>OK</p>
            <p>Warning</p>
            <p>Critical</p>


        </div>
    );
}
