import * as actions from './actionTypes';

let lastId = 0;
let data;
function get_full_data(){
    return fetch('/api/groups')
        .then(r => {
            //console.log(r.json())
            return r.json()
        })
        .catch(err => {
            return err
        })
}


export default function reducer(state = get_full_data().then(result => {
                                                data = result;
                                            }), action) {
console.log('ok', data)
  switch (action.type) {
    case actions.TASK_ADD:
      return [...state, {
        id: ++lastId,
        title: action.payload.title,
        completed: false,
      }];

    case actions.TASK_TOGGLE:
      return state.map(task => {
        if (task.id === action.payload.id)
          return { ...task, completed: !task.completed }
        return task;
      });

    case actions.TASK_REMOVE:
      return state.filter(task => action.payload.id !== task.id);

    default:
      return state;
  }
}
