import * as actions from './actionTypes';

let lastId = 0;

async function get_full_data(){

}
let data = get_full_data()

export default function reducer(state = fetch('/api/groups')
                                                .then(r => {
                                                    //console.log(r.json())
                                                    return r.json()
                                                })
                                                .catch(err => {
                                                    return err
                                                }), action) {
console.log('ok', data)
  switch (action.type) {
    case actions.TASK_ADD:
      return [...state, {
        id: ++lastId,
        title: action.payload.title,
        completed: false,
      }];

    case actions.TASK_TOGGLE:
      return state.map(task => {
        if (task.id === action.payload.id)
          return { ...task, completed: !task.completed }
        return task;
      });

    case actions.TASK_REMOVE:
      return state.filter(task => action.payload.id !== task.id);

    default:
      return state;
  }
}
