import './App.css';
import React from "react";
import { useSelector } from "react-redux";
import FirstColumn from "./components/FirstColumn.jsx"
import SecondColumn from "./components/SecondColumn.jsx"
import ThirdColumn from "./components/ThirdColumn.jsx"
import { CurrentNodeInfo } from "./contexts/CurrentNodeInfo.js"

import { CurrentNodeMetrics } from "./contexts/CurrentNodeInfo.js"

function App() {
    const full_data = useSelector(state => state);
    function con(){
        console.log(full_data)
    }

    const [currentNode, setCurrentNode] = React.useState({});
    const [currentMetrics, setCurrentMetrics] = React.useState({name:'anna'});

    function get_metrics() {
            return fetch('/api/metrics')
                .then(r => {
                    return r.json()
                })
                .catch(err => {
                    return err
                })
     }

    function chooseNodeStat(node) {

        setCurrentNode(node)
        get_metrics().then((metrics) => {
                        const met_arr = []
                        for (const met of metrics) {

                            if (met.caption === node.node_name) {
                                console.log(met.caption, node.node_name)
                                met_arr.push(met)
                                //console.log('m', metricsOfNode)
                            }
                        }
                        setCurrentMetrics([...met_arr]);
                        console.log('m', currentMetrics)
                    }
                )
    }


  return (
    <div className="App">
      <CurrentNodeInfo.Provider value={currentNode}>
        <CurrentNodeMetrics.Provider value={currentMetrics}>
              <FirstColumn />
              <SecondColumn chooseNodeStat={chooseNodeStat}/>
              <ThirdColumn />
          </CurrentNodeMetrics.Provider>
      </CurrentNodeInfo.Provider>
    </div>
  );
}

export default App;
