import React, {useContext} from "react";
import { AllInfo } from "../contexts/CurrentNodeInfo"

export default function Groups(props) {
    const full_data = useContext(AllInfo)

    // function filterNOdeByGroup(group) {
    //
    // }

    const [inf, setInf] = React.useState([])
    const [groups, setGroups] = React.useState([])
    React.useEffect(() => {
        let res = full_data
        setInf(res)
        let un_group = []
        inf.map((i) => {
            let gr = i["group_name"]
            if (un_group.indexOf(gr) === -1) {
                un_group.push(gr)
            }

        })
        //console.log(un_group)
        setGroups(un_group)

    }, [full_data]);

    return (
        <div className="groups">
            {groups.map((group, j) => (
                    <p className="group" key={j} onClick={()=>props.filterFullData(group)}>{group}</p>
            ))}
            <p className="group" onClick={()=>props.filterFullData()}>Все группы</p>
        </div>
    );
}
