import React from "react";
import { useSelector } from "react-redux";

export default function Status() {
    const full_data = useSelector(state => state);
    function getInfo(){

        let statuses = {};
        let status_of_service = {};
        return full_data.then(res=>{
            // eslint-disable-next-line array-callback-return
            res.map((i) => {
                let status = i["status_description"]
                statuses[status] = 1
            })
            if (statuses.hasOwnProperty('CRITICAL')) {
                status_of_service['status'] = 'CRITICAL'
                status_of_service['color'] = 'red'
            }
            else{
                if (statuses.hasOwnProperty('WARNING')) {
                    status_of_service['status'] = 'WARNING'
                    status_of_service['color'] = 'yellow'
                }
                else {
                    status_of_service['status'] = 'OK'
                    status_of_service['color'] = 'green'
                }
            }
            return {
                'status': 'red',
                'color': 'red'
            }
        })
    }

    const [inf, setInf] = React.useState({
        'status': 'red',
        'color': 'red'
    })

    React.useEffect(() => {
        setInf(getInfo().then(res=>{return res}))
        console.log(inf)
    }, []);


    return (
        <div className="status">
            <p>Статус: <span style={{color:inf['color']}}>{inf['status']}</span></p>
        </div>
    );
}
