import React from "react";
import { AllInfo } from "./../contexts/CurrentNodeInfo"

export default function Status() {
    const full_data = React.useContext(AllInfo)

    const [inf, setInf] = React.useState([])
    const [statusOfService, setStatusOfService] = React.useState({
        'status': '',
        'color': ''
    })
    React.useEffect(() => {
        console.log(full_data)
        if (full_data) {
            let res = full_data;
            setInf(res)
                    let statuses = {};
                    inf.map((i) => {
                            let status = i["status_description"]
                            if (statuses.hasOwnProperty(status)) {
                                statuses[status] += 1
                            }
                            else {statuses[status] = 1}

                        })
                        if (statuses.hasOwnProperty('CRITICAL')) {
                            setStatusOfService({
                                'status': 'CRITICAL',
                                'color': 'red'
                            })
                        }
                        else{
                            if (statuses.hasOwnProperty('WARNING')) {
                                setStatusOfService({
                                    'status': 'WARNING',
                                    'color': 'yellow'
                                })
                            }
                            else {
                                setStatusOfService({
                                    'status': 'OK',
                                    'color': 'green'
                                })
                            }
                        }
        }

    }, [full_data]);


    return (
        <div className="status">
            <p >Статус: <span style={{color:`${statusOfService["color"]}`}}>{statusOfService["status"]}</span></p>
        </div>
    );
}
