import * as actions from './actionTypes';

let lastId = 0;

function get_full_data(){
    return fetch('/api/groups')
        .then(r => {
            return r
        })
        .catch(err => {
            return err
        })
}


export default function reducer(state = [], action) {
  switch (action.type) {
    case actions.GET_ALL_INFO_FROM_DB:
        get_full_data().then(res => {
            return res.json().then(res=> {return res})
        })
    case actions.CHOOSE_GROUP:
          return state.then(res => {
            return res.filter(gr => "Москва" === gr['group_name'])
          })
//        return state.then( res => {
//        console.log('old', res)
//        res.filter(gr => "Москва" === gr['group_name'])
//        console.log('new', res)
//        let ans = []
//        for (const node of res) {
//         console.log(node, action.payload.group)
//             if (node['group_name'] === action.payload.group)
//             {ans.push(node)}
//        }
//        console.log('new', ans )
//        return ans
       //});
    case actions.TASK_ADD:
      return [...state, {
        id: ++lastId,
        title: action.payload.title,
        completed: false,
      }];

    case actions.TASK_TOGGLE:
      return state.map(task => {
        if (task.id === action.payload.id)
          return { ...task, completed: !task.completed }
        return task;
      });

    case actions.TASK_REMOVE:
      return state.filter(task => action.payload.id !== task.id);

    default:
      return state;
  }
}
