import React from "react";
import {useSelector} from "react-redux";

export default function FirstColumn() {

    const full_data = useSelector(state => state);

    const [inf, setInf] = React.useState([])
    const [statusOfService, setStatusOfService] = React.useState({
        'UP': 0,
        'DOWN': 0,
        'SHUTDOWN':0,
        'WARNING': 0,
        'CRITICAL': 0
    })
    React.useEffect(() => {

        full_data.then(
            res=> {
                setInf(res)
                //console.log(inf)
                inf.map((i) => {
                    let status = i["status_description"]
                    let num = statusOfService[status]
                    //console.log('num',i["status_description"], num, typeof(num))
                    setStatusOfService(num + 1)
                })
                console.log(statusOfService)
            })

        //console.log('ss',  inf[0]["group_name"])

    }, [inf]);


    return (
        <div className="info">
            <p></p>
            <p>Up</p>
            <p>Down</p>
            <p>OK</p>
            <p>Warning</p>
            <p>Critical</p>


        </div>
    );
}
