import React from "react";
import {useSelector} from "react-redux";


export default function Node(props) {

    const [metrics, setMetrics] = React.useState()

    function get_metrics() {
            return fetch('/api/metrics')
                .then(r => {
                    return r.json()
                })
                .catch(err => {
                    return err
                })
        }

    React.useEffect(() => {
           get_metrics().then((metrics) => {
                 let newest_metric;
                 for (const met of metrics) {

                    if (met.caption === props.node.node_name) {
                        if (newest_metric === undefined) {
                            newest_metric = met
                        }
                        else {
                            if (met.datetime > newest_metric.datetime) {
                                newest_metric = met
                            }
                        }
                    }
                 }
                    setMetrics(newest_metric)
           }
        )
        }, [])

//     React.useEffect(() => {
//
//         full_data.then(
//             res=> {
//                 setInf(res)
//
//             })
//
//     }, [inf]);

     return (
         <div className="node">
            <p className='node__status' style={{backgroundColor:`${props.node.status_color}`}}></p>
            <p className='node_name'>{props.node.node_name}</p>
            { metrics ?
            <div className='node__metrics'>
                <p> CPU: {metrics.cpu_utilization}%</p>
                 <p> RAM: {metrics.memory_utilization}%</p>
                  <p> DISK: {metrics.disk_utilization}%</p>
            </div>
            : null}

         </div>

       )


}
