import React from "react";
import { useSelector } from "react-redux";

export default function Status() {
    const full_data = useSelector(state => state);
    let statuses = {};

    full_data.then(res=>{
        // eslint-disable-next-line array-callback-return
        res.map((i) => {
            if (statuses.hasOwnProperty(i["status_description"])) {
                statuses[i["status_description"]] += 1
            }

            else {
                statuses[i["status_description"]] = 1
            }

        })})
        console.log(statuses['WARNING'])
        let status_of_service = {}

        if (statuses['CRITICAL'] !== 0) {
            status_of_service['status'] = 'CRITICAL'
            status_of_service['color'] = 'red'
        }
        else{
            if (statuses['WARNING'] !== 0) {
                status_of_service['status'] = 'WARNING'
                status_of_service['color'] = 'yellow'
            }
            else {
                status_of_service['status'] = 'OK'
                status_of_service['color'] = 'green'
            }
        }


    return (
        <div className="status">
            <p>Статус: <span style={{color:status_of_service['color']}}>{status_of_service['status']}</span></p>
        </div>
    );
}
